import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TaskPage = () => {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [editMode, setEditMode] = useState(false);
  const [editId, setEditId] = useState(null);

  const fetchTasks = async () => {
    const token = localStorage.getItem('token');
    const res = await axios.get('http://localhost:5000/api/tasks', {
      headers: { Authorization: token }
    });
    setTasks(res.data);
  };

  const createTask = async () => {
    const token = localStorage.getItem('token');
    await axios.post('http://localhost:5000/api/tasks', { title }, {
      headers: { Authorization: token }
    });
    setTitle('');
    fetchTasks();
  };

  const saveUpdatedTask = async () => {
    const token = localStorage.getItem('token');
    await axios.put(`http://localhost:5000/api/tasks/${editId}`, { title }, {
      headers: { Authorization: token }
    });
    setTitle('');
    setEditMode(false);
    setEditId(null);
    fetchTasks();
  };

  const toggleComplete = async (id, completed) => {
    const token = localStorage.getItem('token');
    await axios.put(`http://localhost:5000/api/tasks/${id}`, { completed: !completed }, {
      headers: { Authorization: token }
    });
    fetchTasks();
  };

  const deleteTask = async (id) => {
    const token = localStorage.getItem('token');
    await axios.delete(`http://localhost:5000/api/tasks/${id}`, {
      headers: { Authorization: token }
    });
    fetchTasks();
  };

  const startEdit = (task) => {
    setTitle(task.title);
    setEditId(task._id);
    setEditMode(true);
  };

  useEffect(() => { fetchTasks(); }, []);

  return (
    <div className="p-8">
      <h1 className="text-2xl mb-4">Your Tasks</h1>

      <div className="flex mb-4">
        <input
          className="border p-2 w-full"
          placeholder="New task"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        {editMode ? (
          <button
            onClick={saveUpdatedTask}
            className="bg-green-500 text-white px-4 ml-2"
          >
            Update
          </button>
        ) : (
          <button
            onClick={createTask}
            className="bg-blue-500 text-white px-4 ml-2"
          >
            Add
          </button>
        )}
      </div>

      <ul>
        {tasks.map(task => (
          <li key={task._id} className="flex justify-between items-center mb-2">
            <span
              className={`cursor-pointer flex-1 ${task.completed ? 'line-through text-gray-500' : ''}`}
              onClick={() => toggleComplete(task._id, task.completed)}
            >
              {task.title}
            </span>
            <div className="ml-2 flex gap-2">
              <button
                onClick={() => startEdit(task)}
                className="text-yellow-600"
              >
                Edit
              </button>
              <button
                onClick={() => deleteTask(task._id)}
                className="text-red-500"
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskPage;
