import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TaskPage = () => {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');

  const fetchTasks = async () => {
    const token = localStorage.getItem('token');
    const res = await axios.get('http://localhost:5000/api/tasks', {
      headers: { Authorization: token }
    });
    setTasks(res.data);
  };

  const createTask = async () => {
    const token = localStorage.getItem('token');
    await axios.post('http://localhost:5000/api/tasks', { title }, {
      headers: { Authorization: token }
    });
    setTitle('');
    fetchTasks();
  };

  const updateTask = async (id, completed) => {
    const token = localStorage.getItem('token');
    await axios.put(`http://localhost:5000/api/tasks/${id}`, { completed: !completed }, {
      headers: { Authorization: token }
    });
    fetchTasks();
  };

  const deleteTask = async (id) => {
    const token = localStorage.getItem('token');
    await axios.delete(`http://localhost:5000/api/tasks/${id}`, {
      headers: { Authorization: token }
    });
    fetchTasks();
  };

  useEffect(() => { fetchTasks(); }, []);

  return (
    <div className="p-8">
      <h1 className="text-2xl mb-4">Your Tasks</h1>
      <div className="flex mb-4">
        <input
          className="border p-2 w-full"
          placeholder="New task"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <button onClick={createTask} className="bg-blue-500 text-white px-4 ml-2">Add</button>
      </div>
      <ul>
        {tasks.map(task => (
          <li key={task._id} className="flex justify-between items-center mb-2">
            <span
              className={`cursor-pointer ${task.completed ? 'line-through text-gray-500' : ''}`}
              onClick={() => updateTask(task._id, task.completed)}
            >
              {task.title}
            </span>
            <button onClick={() => deleteTask(task._id)} className="text-red-500 ml-2">Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskPage;
